export function LanguageIcon({ language, className, size = "md" }: LanguageIconProps) {
  const sizeClass = sizeClasses[size];

  return (
    <LanguageSvg language={language} className={cn(sizeClass, className)} />
  );
}

// Helper component for language badge with icon
interface LanguageBadgeProps {
  language: string;
  percentage?: number;
  className?: string;
}

export function LanguageBadge({ language, percentage, className }: LanguageBadgeProps) {
  return (
    <div className={cn("inline-flex items-center gap-1.5 px-2 py-1 rounded-md bg-white/5 border border-white/10 text-sm", className)}>
      <LanguageIcon language={language} size="sm" />
      <span className="text-muted-foreground">{language}</span>
      {percentage !== undefined && (
        <span className="text-xs text-muted-foreground/70">({percentage}%)</span>
      )}
    </div>
  );
}
